    this.watermarkage = function()
    {
        if(Application.current.watermark)
        {
            var scrollDivisor = 125;

            var container = new createjs.Container();
            var logo = new createjs.Bitmap("http://ten90studios.com/images/layouts/white_logo_small.png");
            logo.x = stageWidth - 190;
            logo.y = -30;
            logo.alpha = .3;
            container.addChild(logo);

            logo.listen("click", function(){
                location.href = "http://ten90studios.com";
            });

            var strings =
			[
				"This site is for review purposes only for work in progress by Ten 90 Studios.",
				"This site may contain confidential and proprietary content and is protected from disclosure.",
				"Distribution or disclosure of url, logins or passwords without prior written consent from Ten 90 Studios may violate non-disclosure or contractual agreements.",
				"Do not copy, cite, or distribute.",
				" 2016 Ten 90 Group, Inc. All rights reserved."
			];

            var text = Make.text(container, strings[0], "bold 48px Arial", "#FFF", 15, 55, .3, "left");
            Make.shapeRect(0, 0, stageWidth - 232, 200, "#000", 1, null, text);

            if(text.width < stageWidth - 200)
                text.x = (stageWidth - text.width) / 2;

            container.y = stageHeight - 115;
            stage.addChild(container);

            var textIndex = 0;
            function loopage()
            {
                text.x = 15;

                textIndex++;
                if(textIndex >= strings.length) textIndex = 0;
                text.text = strings[textIndex];

                if(text.width < stageWidth - 200)
                    text.x = (stageWidth - text.width) / 2;

                tween.vars.x = -text.width;
                tween.duration(text.width / scrollDivisor);
                timeline.invalidate();
                timeline.pause(0, true);
                timeline.play();
            }

            function toTheMoon()
            {
                container.setIndex(stage.numChildren - 1);
            }

            var timeline = new TimelineMax({onComplete:loopage, onUpdate:toTheMoon});
            timeline.add(text.from(2, {y:"+=50", alpha:0, ease:Quad.easeInOut}));

            var tween = text.to(text.width / scrollDivisor, {delay:2, x:-text.width, ease:Linear.easeNone});
            timeline.add(tween);
        }
    };

