/**
 *
 * @constructor
 */
var Flow = new function()
{
	/**
	 * 
	 * @param {createjs.DisplayObject} target
	 * @param {Number} duration
	 * @param {Array} points
	 * @param {Number} [delay=0]
	 * @param {Number} [repeatDelay=.1]
	 * @param {Number} [repeat=-1]
	 * @param {Function} [onRepeat=null]
	 * @param {Function} [onComplete=null]
	 * @returns {TimelineMax}
	 */
	this.through = function(target, duration, points, delay, repeatDelay, repeat, onRepeat, onComplete, onPoint)
	{
		delay = setDefault(delay, 0);
		repeatDelay = setDefault(repeatDelay, .1);
		repeat = setDefault(repeat, -1);
		onRepeat = setDefault(onRepeat, function(){});
		onComplete = setDefault(onComplete, function(){});
		onPoint = setDefault(onPoint, function(){});

		var i;
		var distances = [];
		var totalDistance = 0;

		for(i = 0; i < points.length; i++)
		{
			var x1 = points[i][0].x;
			var y1 = points[i][0].y;
			var x2 = points[i][1].x;
			var y2 = points[i][1].y;

			var xDistance = Math.abs(x2 - x1);
			var yDistance = Math.abs(y2 - y1);
			var distance = xDistance + yDistance;

			totalDistance += distance;
			distances.push(distance);
		}

		var durations = [];
		for(i = 0; i < distances.length; i++)
		{
			var percentage = distances[i] / totalDistance;
			var tweenDuration = duration * percentage;
			durations.push(tweenDuration);
		}

		var timeline = new TimelineMax({repeat:repeat, delay:delay, repeatDelay:repeatDelay, onRepeat:onRepeat, onComplete:onComplete});
		timeline.insert(target.to(.01, {scale:0}));

		for(i = 0; i < points.length; i++)
		{
			var startPoint = points[i][0];
			var endPoint = points[i][1];

			reset(target, startPoint);

			var subtimeline = new TimelineMax({delay:.05, onComplete:onPoint});
			subtimeline.add(target.to(.001, {onComplete:reset, onCompleteParams:[target, startPoint]}));
			subtimeline.insert(target.to(.25, {delay:.001, scale:1, alpha:1}));
			subtimeline.insert(target.to(durations[i], {delay:.125, x:endPoint.x, y:endPoint.y, ease:Linear.easeNone}));
			subtimeline.insert(target.to(.25, {delay:durations[i], scale:.25, alpha:0}));
			timeline.add(subtimeline);
		}

		return timeline;
	};

	function reset(target, point)
	{
		target.x = point.x;
		target.y = point.y;
		target.scale = 0;
		target.alpha = 0;
	}
};


