                for(var k = 0; k < Application.current.componentContainer.numChildren; k++)
                {
					var appChild = Application.current.componentContainer.getChildAt(k);
                    if(appChild.parentScene !== this) appChild.destroy();
                }

				var componentContainer = new createjs.Container();
