	this.createMask = function(obj, x, y, shapeName)
	{
		var width, height;
		try
		{
			width = obj.getBounds().width;
			height = obj.getBounds().height;
		}
		catch (e)
		{
			console.log("Width and height were not able to be set - they are now initialized to 0");
			width = height = 0;
		}

		if (typeof x === 'undefined') x = 0;
		if (typeof y === 'undefined') y = 0;

		var graphic;
		var shape;
		//console.log("Creating mask: " + shapeName);
		switch (shapeName)
		{
			case "plus":
				graphic = new createjs.Graphics();
				graphic.beginStroke("#000000");
				graphic.mt(0, 0).lt(0, height * -.5).lt(width, height * -.5).lt(width, 0)
					.lt(width * 1.5, 0).lt(width * 1.5, 0).lt(width * 1.5, height).lt(width, height)
					.lt(width, height * 1.5).lt(0, height * 1.5).lt(0, height)
					.lt(width * -.5, height).lt(width * -.5, 0).lt(0, 0);
				graphic.beginFill("#000000");
				shape = new createjs.Shape(graphic);
				break;

			case "circle":
				graphic = new createjs.Graphics().beginFill("#000000").drawCircle((stageWidth / 2), (stageHeight / 2), 1.25 * ((width > height) ? width : height));
				shape = new createjs.Shape(graphic);
				break;

			default:
				graphic = new createjs.Graphics();
				graphic.beginStroke("#000000");
				graphic.mt(0, 0).lt(stageWidth, 0).lt(stageWidth, stageHeight).lt(0, stageHeight).lt(0, 0);
				graphic.beginFill("#000000");

				//graphic = new createjs.Graphics().beginFill("#000000").drawRect(width / 2, height / 2, width, height);
				shape = new createjs.Shape(graphic);
				break;
		}

		shape.x = obj.x;
		shape.y = obj.y;
		obj.mask = shape;
		// Initial x and y are set to center of parent.  The below changes it to the top left corner and adds input params.
		obj.mask.x -= (width / 2) + x;
		obj.mask.y -= (height / 2) + y;
	};
