	this.properties.duration = setDefault(properties.duration, 1);
	this.properties.durationVariance = setDefault(properties.durationVariance, 0);

	this.properties.maxParticles = setDefault(properties.maxParticles, 100);

	this.properties.startX = setDefault(properties.startX, 0);
	this.properties.startY = setDefault(properties.startY, 0);
	this.properties.startXVariance = setDefault(properties.startXVariance, 0);
	this.properties.startYVariance = setDefault(properties.startYVariance, 0);

	this.properties.endX = setDefault(properties.endX, 0);
	this.properties.endY = setDefault(properties.endY, 0);
	this.properties.endXVariance = setDefault(properties.endXVariance, 0);
	this.properties.endYVariance = setDefault(properties.endYVariance, 0);

	this.properties.startAlpha = setDefault(properties.startAlpha, 1);
	this.properties.startAlphaVariance = setDefault(properties.startAlphaVariance, 0);

	this.properties.endAlpha = setDefault(properties.endAlpha, 0);
	this.properties.endAlphaVariance = setDefault(properties.endAlphaVariance, 0);

	this.properties.startScale = setDefault(properties.startScale, 1);
	this.properties.startScaleVariance = setDefault(properties.startScaleVariance, 0);

	this.properties.endScale = setDefault(properties.endScale, 0);
	this.properties.endScaleVariance = setDefault(properties.endScaleVariance, 0);

	this.properties.multiplier = setDefault(properties.multiplier, 1);
	this.properties.iteration = setDefault(properties.iteration, .01);
	this.properties.ease = setDefault(properties.ease, TweenMax.defaultEase);

	this.balls = [];
	this.tweens = [];
	this.ballIndex = 0;
	this.totalBalls = this.properties.maxParticles;
	this.container = Make.container(parent);

	for(var i = 0; i < this.totalBalls; i++)
	{
		var imageName = this.properties.images[Math.floor(Math.random() * this.properties.images.length)];
		var ball = Make.bitmap(imageName);
		ball.regX = ball.regY = ball.height / 2;
		this.balls.push(ball);
	}

