var RotateScreen = {};

RotateScreen.init = function(orientation, image, color)
{
	RotateScreen.graphic = image;
	RotateScreen.isShowing = false;

	RotateScreen.show = function ()
	{
		if(RotateScreen.isShowing) return;

		RotateScreen.isShowing = true;

		Audio.pauseAll();

		Application.current.rotateBG = Make.element("div",
		{
			parent:document.body,
			style:
			{
				position:"absolute",
				backgroundColor:color,
				top:0,
				left:0,
				width:"100%",
				height:"100%"
			}
		});

		Application.current.rotateGraphic = Make.element("img",
		{
			parent:document.body,
			src:RotateScreen.graphic,
			style:
			{
				position:"absolute",
				top:"50%",
				left:"50%",
				maxWidth:"100%",
				maxHeight:"100%",
				transform:"translate(-50%, -50%)"
			}
		});

		Application.current.canvas.style.visibility = "hidden";
		Application.current.stopRender();
	};

	RotateScreen.hide = function()
	{
		if(!RotateScreen.isShowing) return;

		RotateScreen.isShowing = false;

		Audio.resumeAll();

		if(Application.current.rotateGraphic)
		{
			document.body.removeChild(Application.current.rotateGraphic);
			Application.current.rotateGraphic = null;

			document.body.removeChild(Application.current.rotateBG);
			Application.current.rotateBG = null;
		}

		Application.current.canvas.style.visibility = "visible";
		Application.current.startRender();
	};
};

