/**
 *
 * @class Scene
 * @extends createjs.DisplayObject
 * @constructor
 */
var Scene = new function()
{
	this.componentList = [];
	
	/**
	 *
	 * @param Component
	 * @param scenes
	 * @param excludedScenes
	 * @param addToScene
	 */
	this.addComponent = function(Component, scenes, excludedScenes, addToScene)
	{
		if(scenes && scenes.length > 0)
		{
			for(var i = 0; i < scenes.length; i++)
			{
				var className = scenes[i].name;
				if(!this.componentList[className]) this.componentList[className] = [];
				this.componentList[className].push({component:Component, addToScene:addToScene});
			}
		}
		else
		{
			if(!this.componentList["allScenes"]) this.componentList["allScenes"] = [];
			if(!this.componentList["excludedScenes"]) this.componentList["excludedScenes"] = [];
            if(!this.componentList["addToScene"]) this.componentList["addToScene"] = [];
			
			this.componentList["allScenes"].push(Component);
			this.componentList["excludedScenes"].push(excludedScenes);
            this.componentList["addToScene"].push(addToScene);
		}
	};

};


