	/**
	 * Executed after any scene is done with its transition
	 * @type {Function}
	 */
	this.onTransitionComplete = null;

	/**
	 * Scene that is transitioning away
	 * @type {Scene}
	 */
	this.leavingScene = null;

	this.stageCenter  = function(){return new createjs.Point(stageWidth / 2, stageHeight / 2)};
	this.stageTop     = function(){return -stageHeight / 2};
	this.stageBottom  = function(){return stageHeight * 1.5};
	this.stageLeft    = function(){return -stageWidth / 2};
	this.stageRight   = function(){return stageWidth * 1.5};

