var SpeechRecognition = new function()
{
	var recognition;
	var interimCallback;

	this.start = function(commands, onData)
	{
		commands = setDefault(commands, {"testing":console.log.apply("1, 2, 3")});
		interimCallback = onData;

		annyang.addCommands(commands);
		annyang.start();

		recognition = annyang.getSpeechRecognizer();
		recognition.interimResults = true;
		recognition.onresult = this.results.bind(this);
	};

	this.results = function(event)
	{
		var interim = '';
		var finalString = '';

		for (var i = event.resultIndex; i < event.results.length; ++i)
		{
			if (event.results[i].isFinal)
			{
				finalString += event.results[i][0].transcript;
				annyang.trigger(finalString);
			}
			else
			{
				interim += event.results[i][0].transcript;
				if(interimCallback) interimCallback(interim);
			}
		}
	};

	this.stop = function()
	{
		annyang.stop();
	};
};



/**
 * @name SpriteVideo
 * @namespace SpriteVideo
 * @extends createjs.DisplayObject
 * @param videoID {String} - Filename without extension
 * @param parent {createjs.DisplayObject} - Parent in which the SpriteVideo object will be added
 * @param xPos=0 {Number}
 * @param yPos=0 {int}
 * @param index=0 {int}
 * @param autoplay=true {Boolean}
 * @param loop=true {Boolean}
 * @param autoDispose=true {Boolean}
 * @param onComplete=null {Function}
 * @constructor
 */
(function()
{
	function SpriteVideo(videoID, parent, xPos, yPos, index, autoplay, loop, autoDispose, onComplete)
	{
		this.Container_constructor();

	    xPos 	 	= setDefault(xPos, 0);
		yPos 	 	= setDefault(yPos, 0);
		autoplay 	= setDefault(autoplay, true);
		loop 	 	= setDefault(loop, true);
		index 	 	= setDefault(index, parent ? parent.numChildren : 0);
        autoDispose = setDefault(autoDispose, true);
		onComplete 	= setDefault(onComplete, null);

		this.video = Application.current.videos[videoID];
		
        if(loop) this.video.loop = true;
		if(onComplete) this.video.onended = onComplete;

		this.setBounds(0, 0, this.video.videoWidth, this.video.videoHeight);

		this.canvas = document.createElement("canvas");
		this.canvas.width = this.video.videoWidth;
		this.canvas.height = this.video.videoHeight;

		this.ctx = this.canvas.getContext("2d");
		this.ctx.drawImage(this.video, 0, 0);

		this.poster = new createjs.Bitmap(this.canvas);
		this.poster.mouseEnabled = false;
        this.addChild(this.poster);

		this.bitmap = new createjs.Bitmap(this.video);
		this.bitmap.mouseEnabled = false;
		this.addChild(this.bitmap);

        this.x = xPos;
        this.y = yPos;

		var parentRemoved = (function()
		{
			if(this.bitmap)
			{
				if(autoDispose) this.dispose();
				else if(this.video) this.pause();
			}

			parent.removeEventListener("removed", parentRemoved);

		}).bind(this);

		parent.addEventListener("removed", parentRemoved);

		if(autoplay) this.video.play();
		if(parent) parent.addChildAt(this, index);
	}

	var prototype = createjs.extend(SpriteVideo, createjs.Container);

	/**
	 *
	 */
	prototype.play = function()
	{
		this.video.play();
	};

	/**
	 *
	 */
	prototype.pause = function()
	{
		this.video.pause();
	};

	/**
	 *
	 * @returns {SpriteVideo.currentTime|*|number|Number}
	 */
	prototype.currentTime = function()
	{
		return this.video.currentTime;
	};

	/**
	 *
	 * @returns {boolean}
	 */
	prototype.isFinished = function()
	{
		return this.video.currentTime >= this.video.duration;
	};

	/**
	 *
	 * @param time
	 * @param useFrame
	 */
	prototype.seek = function(time, useFrame)
	{
		useFrame = typeof useFrame === "undefined" ? true : useFrame;

		if(useFrame)
		{
			this.ctx.drawImage(this.video, 0, 0);
			this.poster.image = this.canvas;
			this.addChildAt(this.poster, this.getChildIndex(this.bitmap));

			Quick.call(.25, function() {
				this.removeChild(this.poster);
			}, null, this);
		}

		this.video.currentTime = time;
	};

	/**
	 *
	 * @param videoPath
	 */
	prototype.swap = function(videoPath)
	{
		this.video.pause();
		this.video.currentTime = 0;

		this.video = Application.current.videos[videoPath];
		this.bitmap.image = this.video;
		this.canvas.getContext("2d").clearRect(0, 0, this.canvas.width, this.canvas.height);
		this.canvas.width = this.video.videoWidth;
		this.canvas.height = this.video.videoHeight;
		this.canvas.getContext("2d").drawImage(this.video, 0, 0);

		this.poster.image = this.canvas;

        this.addChildAt(this.poster, this.getChildIndex(this.bitmap));

		this.video.play();
	};

	/**
	 *
	 * @param e
	 */
	prototype.dispose = function(e)
	{
		//this.video.removeEventListener("ended", onComplete);

		if(this.video)
		{
			this.video.pause();
			this.video.currentTime = 0;
		}

		this.removeAllChildren();
		this.removeAllEventListeners();

		this.canvas = this.poster = this.bitmap = this.ctx = this.video = null;

		if(this.parent) this.parent.removeChild(this);
	};

	window.SpriteVideo = createjs.promote(SpriteVideo, "Container");
	
}());
